mapper.select:
SELECT g.*
FROM garden g
WHERE g.latitude IS NOT NULL
  AND g.longitude IS NOT NULL
  AND (
          2 * 6371000 * ASIN(
                  SQRT(
                          POWER(SIN(RADIANS((#{latitude} - g.latitude) / 2)), 2) +
                          COS(RADIANS(#{latitude})) * COS(RADIANS(g.latitude)) *
                          POWER(SIN(RADIANS((#{longitude} - g.longitude) / 2)), 2)
                  )
                        )
          )  <=  #{radius}
ORDER BY (
             2 * 6371000 * ASIN(
                     SQRT(
                             POWER(SIN(RADIANS((#{latitude} - g.latitude) / 2)), 2) +
                             COS(RADIANS(#{latitude})) * COS(RADIANS(g.latitude)) *
                             POWER(SIN(RADIANS((#{longitude} - g.longitude) / 2)), 2)
                     )
                           )
             ) ASC LIMIT 200
